parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "jobTitle": {
      "type": "string",
      "description": "The full title of the job."
    },
    "jobType": {
      "type": "string",
      "description": "Classification of the job (e.g., Full-time, Part-time, Contract, Internship, Temporary, or other classifications if available)."
    },
    "salary": {
      "type": "string",
      "description": "The salary or pay range indicated for the position (if available)."
    },
    "datePosted": {
      "type": "string",
      "description": "The date the job listing was posted (if available), in a recognizable date format."
    },
    "jobDescriptionSnippet": {
      "type": "string",
      "description": "A concise summary or the first few sentences of the job description."
    },
    "jobLink": {
      "type": "string",
      "description": "The URL of the original job listing."
    }
  },
  "required": ["jobTitle", "jobLink"]
}
